mapper.select:
select count(*)
	from LOGIN
where
	LOGIN_ID = #{loginId}
	AND PASSWORD = #{password}

mapper.update:
update
	LOGIN
set
	FAILCOUNT = #{failcount}
	,FAILCOUNTTOTAL= #{failcounttotal}
	,LOCKFLAG = #{lockflag}
	,LOGINIPADDRESS = #{loginipaddress}
	,LATESTLOGINDATETIME = #{latestlogindatetime}
	,BLOCKDATE = #{blockdate}
where
	LOGIN_ID = #{loginId}
