mapper.insert:

INSERT INTO site 
	( 
	   user_seq
	  , url
	  , version
	  , file_name
	  , file_id
	  , ctime
	  , cdate
	 )
VALUES (#{userSeq}
	  , #{url}
	  , #{version}
	  , #{fileName}
	  , #{fileId}
	  , #{ctime}
	  , #{cdate}
	   )

mapper.update:

UPDATE SITE
SET 
	URL=#{url}
	, VERSION=#{version}
	, FILE_NAME=#{fileName}
	, FILE_ID=#{fileId}
	, UTIME=#{utime}
	, UDATE=#{udate}
WHERE 
	DTIME = 0 AND DDATE = 0 AND SEQ=#{seq}

mapper.delete:

UPDATE SITE
SET 
	DDATE=#{ddate},
	DTIME =#{dtime}			
WHERE 
	SEQ=#{seq}

mapper.select:
SELECT SEQ , USER_SEQ , URL , VERSION , FILE_NAME , FILE_ID , CTIME , CDATE , UTIME , UDATE , DTIME , DDATE FROM SITE WHERE 1=1
AND SEQ = #{searchKeyword}
AND USER_SEQ LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY SEQ limit #{recordCountPerPage} offset #{firstIndex}

SELECT SEQ , USER_SEQ , URL , VERSION , FILE_NAME , FILE_ID , CTIME , CDATE , UTIME , UDATE , DTIME , DDATE FROM SITE WHERE 1=1 AND USER_SEQ = #{userSeq}
AND SEQ = #{searchKeyword}
AND USER_SEQ LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY SEQ limit #{recordCountPerPage} offset #{firstIndex}

SELECT COUNT(*) totcnt FROM SITE WHERE 1=1
AND seq = #{searchKeyword}
AND user_seq LIKE CONCAT('%',#{searchKeyword},'%')

SELECT COUNT(*) totcnt FROM SITE WHERE 1=1 AND USER_SEQ = #{userSeq}
AND seq = #{searchKeyword}
AND user_seq LIKE CONCAT('%',#{searchKeyword},'%')
